-- Create Database --------------------------------------------------------------------------------------------------------
DROP DATABASE IF EXISTS myshipmentdelivery;
CREATE DATABASE myshipmentdelivery;
USE myshipmentdelivery;
 
-- Create Tables ----------------------------------------------------------------------------------------------------------
CREATE TABLE `myshipmentdelivery`.`tblm_role` ( `role_id` INT(10) NOT NULL AUTO_INCREMENT, `role_name` VARCHAR(30) NOT NULL, PRIMARY KEY (`role_id`));

CREATE TABLE `myshipmentdelivery`.`tblm_country` ( `country_id` INT(10) NOT NULL AUTO_INCREMENT, `country_name` VARCHAR(30) NOT NULL, PRIMARY KEY (`country_id`));

CREATE TABLE `myshipmentdelivery`.`tblm_state` ( `state_id` INT(10) NOT NULL AUTO_INCREMENT, `country_id` INT(10) NOT NULL, `state_name` VARCHAR(30) NOT NULL, PRIMARY KEY (`state_id`) , INDEX `fk_country_id` (`country_id` ASC), CONSTRAINT `fk_country_id`
                                                FOREIGN KEY (`country_id`) REFERENCES `myshipmentdelivery`.`tblm_country` (`country_id`) ON
                                                DELETE NO ACTION ON
                                                UPDATE NO ACTION);

CREATE TABLE `myshipmentdelivery`.`tblm_city` ( `city_id` INT(10) NOT NULL AUTO_INCREMENT, `state_id` INT(10) NOT NULL, `city_name` VARCHAR(30) NOT NULL, PRIMARY KEY (`city_id`) , INDEX `fk_state_id` (`state_id` ASC), CONSTRAINT `fk_state_id`
                                               FOREIGN KEY (`state_id`) REFERENCES `myshipmentdelivery`.`tblm_state` (`state_id`) ON
                                               DELETE NO ACTION ON
                                               UPDATE NO ACTION);

CREATE TABLE `myshipmentdelivery`.`tblm_pincode` ( `pincode_id` INT(10) NOT NULL AUTO_INCREMENT, `city_id` INT(10) NOT NULL, `pin_code` INT(10) NOT NULL, PRIMARY KEY (`pincode_id`) , INDEX `fk_city_id` (`city_id` ASC), CONSTRAINT `fk_city_id`
                                                  FOREIGN KEY (`city_id`) REFERENCES `myshipmentdelivery`.`tblm_city` (`city_id`) ON
                                                  DELETE NO ACTION ON
                                                  UPDATE NO ACTION);

CREATE TABLE `myshipmentdelivery`.`tblm_menu` ( `menu_id` INT(10) NOT NULL AUTO_INCREMENT, `roll_id` INT(10) NOT NULL, `menu_url` VARCHAR(100) NOT NULL, `display_order` INT(3) NULL, `delete_flag` INT(1) NULL DEFAULT 0, PRIMARY KEY (`menu_id`) , INDEX `fk_role_id` (`roll_id` ASC), CONSTRAINT `fk_role_id`
                                               FOREIGN KEY (`roll_id`) REFERENCES `myshipmentdelivery`.`tblm_role` (`role_id`) ON
                                               DELETE NO ACTION ON
                                               UPDATE NO ACTION);

CREATE TABLE `myshipmentdelivery`.`tblt_user` ( `user_id` INT(10) NOT NULL AUTO_INCREMENT, `role_id` INT(10) NOT NULL, `username` VARCHAR(30) NOT NULL, `password` VARCHAR(500) NULL, `mobile_number` INT(15) NOT NULL, `email_id` VARCHAR(30) NOT NULL, `dob` TIMESTAMP NULL, `gender` ENUM('M','F','U') NULL, `image_url` VARCHAR(100) NULL, `first_name` VARCHAR(20) NULL, `last_name` VARCHAR(20) NULL, `user_status` VARCHAR(20) NOT NULL DEFAULT 'INACTIVE', `created_by` INT(10) NOT NULL, `updated_by` INT(10) NULL, `creation_date` TIMESTAMP NOT NULL, `updation_date` TIMESTAMP NULL, PRIMARY KEY (`user_id`) , INDEX `fk_role_id` (`role_id` ASC), UNIQUE INDEX `username_UNIQUE` (`username` ASC), CONSTRAINT `fk_role_id1`
                                               FOREIGN KEY (`role_id`) REFERENCES `myshipmentdelivery`.`tblm_role` (`role_id`) ON
                                               DELETE NO ACTION ON
                                               UPDATE NO ACTION);

CREATE TABLE `myshipmentdelivery`.`tblt_order` ( `order_id` INT(10) NOT NULL AUTO_INCREMENT, `username` VARCHAR(30) NOT NULL, `user_id` INT(10) NOT NULL, `order_date` TIMESTAMP NOT NULL, `order_status` VARCHAR(20) NULL, `pickup_date` TIMESTAMP NULL, `delivery_date` TIMESTAMP NULL, `moderator_ids` VARCHAR(100) NULL, `courier_uid` VARCHAR(20) NULL, `height` FLOAT(5,2) NULL, `width` FLOAT(5,2) NULL, `depth` FLOAT(5,2) NULL, `weight` FLOAT(6,3) NOT NULL DEFAULT 0.050 , `unique_token` INT(10) NOT NULL, PRIMARY KEY (`order_id`) , INDEX `fk_user_id` (`user_id` ASC), CONSTRAINT `fk_user_id`
                                                FOREIGN KEY (`user_id`) REFERENCES `myshipmentdelivery`.`tblt_user` (`user_id`) ON
                                                DELETE NO ACTION ON
                                                UPDATE NO ACTION);

CREATE TABLE `myshipmentdelivery`.`tblt_address` ( `address_id` INT(10) NOT NULL AUTO_INCREMENT, `user_id` INT(10) NULL, `order_id` INT(10) NULL, `address_line1` VARCHAR(50) NULL, `address_line2` VARCHAR(50) NULL, `pincode` INT(10) NULL, `city` VARCHAR(30) NULL, `state` VARCHAR(30) NULL, `country` VARCHAR(30) NULL, `address_type` VARCHAR(30) NULL, `latitude` FLOAT(10,7) NULL, `longitude` FLOAT(10,7) NULL, `creation_date` TIMESTAMP NOT NULL, `updation_date` TIMESTAMP NULL, `created_by` INT(10) NOT NULL, `updated_by` INT(10) NULL, PRIMARY KEY (`address_id`) , INDEX `fk_user_id1` (`user_id` ASC), INDEX `fk_order_id1` (`order_id` ASC), CONSTRAINT `fk_user_id1`
                                                  FOREIGN KEY (`user_id`) REFERENCES `myshipmentdelivery`.`tblt_user` (`user_id`) ON
                                                  DELETE NO ACTION ON
                                                  UPDATE NO ACTION, CONSTRAINT `fk_order_id1`
                                                  FOREIGN KEY (`order_id`) REFERENCES `myshipmentdelivery`.`tblt_order` (`order_id`) ON
                                                  DELETE NO ACTION ON
                                                  UPDATE NO ACTION);

CREATE TABLE `myshipmentdelivery`.`tblt_branch` ( `branch_id` INT(10) NOT NULL AUTO_INCREMENT, `branch_admin_id` INT(10) NULL, `address_id` INT(10) NOT NULL, `branch_name` VARCHAR(50) NOT NULL, `creation_date` TIMESTAMP NOT NULL, `updation_date` TIMESTAMP NULL, `created_by` INT(10) NOT NULL, `updated_by` INT(10) NULL, PRIMARY KEY (`branch_id`) , INDEX `fk_branch_admin_id` (`branch_admin_id` ASC), INDEX `fk_address_id` (`address_id` ASC), UNIQUE INDEX `branch_name_UNIQUE` (`branch_name` ASC), CONSTRAINT `fk_branch_admin_id`
                                                 FOREIGN KEY (`branch_admin_id`) REFERENCES `myshipmentdelivery`.`tblt_user` (`user_id`) ON
                                                 DELETE NO ACTION ON
                                                 UPDATE NO ACTION, CONSTRAINT `fk_address_id`
                                                 FOREIGN KEY (`address_id`) REFERENCES `myshipmentdelivery`.`tblt_address` (`address_id`) ON
                                                 DELETE NO ACTION ON
                                                 UPDATE NO ACTION);

CREATE TABLE `myshipmentdelivery`.`tblt_rating` ( `rating_id` INT(10) NOT NULL AUTO_INCREMENT, `order_id` INT(10) NOT NULL, `rating` INT(1) NOT NULL DEFAULT 0, PRIMARY KEY (`rating_id`) , INDEX `fk_order_id2` (`order_id` ASC), CONSTRAINT `fk_order_id2`
                                                 FOREIGN KEY (`order_id`) REFERENCES `myshipmentdelivery`.`tblt_order` (`order_id`) ON
                                                 DELETE NO ACTION ON
                                                 UPDATE NO ACTION);

CREATE TABLE `myshipmentdelivery`.`tblt_comment` ( `comment_id` INT(10) NOT NULL AUTO_INCREMENT, `order_id` INT(10) NOT NULL, `comment` VARCHAR(500) NOT NULL, PRIMARY KEY (`comment_id`) , INDEX `fk_order_id3` (`order_id` ASC), CONSTRAINT `fk_order_id3`
                                                  FOREIGN KEY (`order_id`) REFERENCES `myshipmentdelivery`.`tblt_order` (`order_id`) ON
                                                  DELETE NO ACTION ON
                                                  UPDATE NO ACTION);

CREATE TABLE `myshipmentdelivery`.`tblt_chat_log` ( `chat_log_id` INT(10) NOT NULL AUTO_INCREMENT, `chat_type` VARCHAR(30) NOT NULL, `user_id1` INT(10) NOT NULL, `user_id2` INT(10) NOT NULL, `delete_flag_uid1` INT(1) NULL DEFAULT 0, `delete_flag_uid2` INT(1) NULL DEFAULT 0, PRIMARY KEY (`chat_log_id`));

CREATE TABLE `myshipmentdelivery`.`tblt_chat_log_content` ( `chat_log_content_id` INT(10) NOT NULL AUTO_INCREMENT, `chat_log_id` INT(10) NOT NULL, `chat_content` VARCHAR(500) NOT NULL, `chat_time` TIMESTAMP NOT NULL, `user_id` INT(10) NOT NULL, PRIMARY KEY (`chat_log_content_id`) , INDEX `fk_chat_log_id` (`chat_log_id` ASC), INDEX `fk_user_id1` (`user_id` ASC), CONSTRAINT `fk_chat_log_id`
                                                           FOREIGN KEY (`chat_log_id`) REFERENCES `myshipmentdelivery`.`tblt_chat_log` (`chat_log_id`) ON
                                                           DELETE NO ACTION ON
                                                           UPDATE NO ACTION, CONSTRAINT `fk_user_id2`
                                                           FOREIGN KEY (`user_id`) REFERENCES `myshipmentdelivery`.`tblt_user` (`user_id`) ON
                                                           DELETE NO ACTION ON
                                                           UPDATE NO ACTION);

CREATE TABLE `myshipmentdelivery`.`tblt_moderator_wages` ( `moderator_wages_id` INT(10) NOT NULL AUTO_INCREMENT, `moderator_id` INT(10) NOT NULL, `amount` INT(10) NOT NULL, `creation_date` TIMESTAMP NOT NULL, `updation_date` TIMESTAMP NULL, `created_by_ba_id` INT(10) NOT NULL, `updated_by_ba_id` INT(10) NULL, PRIMARY KEY (`moderator_wages_id`) , INDEX `fk_moderator_id` (`moderator_id` ASC), CONSTRAINT `fk_moderator_id`
                                                          FOREIGN KEY (`moderator_id`) REFERENCES `myshipmentdelivery`.`tblt_user` (`user_id`) ON
                                                          DELETE NO ACTION ON
                                                          UPDATE NO ACTION);

CREATE TABLE `myshipmentdelivery`.`tblt_wages` ( `wages_id` INT(10) NOT NULL AUTO_INCREMENT, `moderator_id` INT(10) NOT NULL, `order_id` INT(10) NOT NULL, `amount` INT(10) NOT NULL, `creation_date` TIMESTAMP NOT NULL, PRIMARY KEY (`wages_id`) , INDEX `fk_order_id4` (`order_id` ASC), CONSTRAINT `fk_order_id4`
                                                FOREIGN KEY (`order_id`) REFERENCES `myshipmentdelivery`.`tblt_order` (`order_id`) ON
                                                DELETE NO ACTION ON
                                                UPDATE NO ACTION);

CREATE  TABLE `myshipmentdelivery`.`tblm_notification_template` (`id` INT(10) NOT NULL AUTO_INCREMENT ,`category` VARCHAR(250) NOT NULL ,`template` MEDIUMTEXT NULL ,
  `template_mode` ENUM('email','sms') NOT NULL ,`enable` TINYINT(1) NULL DEFAULT 0 ,`subject` VARCHAR(250) NULL ,`language` VARCHAR(50) NULL ,PRIMARY KEY (`id`) );
									
CREATE  TABLE `myshipmentdelivery`.`tblm_contact` (`contact_id` INT(10) NOT NULL AUTO_INCREMENT ,`name` VARCHAR(30) NOT NULL ,`email_id` VARCHAR(30) NOT NULL ,`phone_number` INT(15) NULL ,`message` VARCHAR(1000) NOT NULL ,
  `user_id` INT(10) NULL ,`is_subscribed` TINYINT(1) NOT NULL DEFAULT 0 ,`contact_date` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ,PRIMARY KEY (`contact_id`) );
								
CREATE  TABLE `myshipmentdelivery`.`tblt_session` (`session_id` INT(10) NOT NULL ,`session` VARCHAR(200) NOT NULL ,`user_id` INT(10) NOT NULL ,`creation_time` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ,PRIMARY KEY (`session_id`, `user_id`) );
ALTER TABLE `myshipmentdelivery`.`tblt_session` CHANGE COLUMN `session_id` `session_id` INT(10) NOT NULL AUTO_INCREMENT  ;

							
